import React, { useState } from 'react';
import { uploadResume, matchJobs } from '../api';
import ExperienceList from './ExperienceList';

export default function UploadResume() {
  const [form, setForm] = useState({ name: '', skills: '', education: '' });
  const [candidate, setCandidate] = useState(null);
  const [jobs, setJobs] = useState([]);
  const [error, setError] = useState('');

  const handleUpload = async () => {
    setError('');
    try {
      const res = await uploadResume(form);
      setCandidate(res.data);
      const m = await matchJobs(res.data._id);
      setJobs(m.data);
    } catch (e) {
      setError(e.response?.data?.error || 'Upload failed');
    }
  };

  return (
    <div>
      <h2>Resume / Candidate</h2>
      <input placeholder="Name" value={form.name} onChange={e=>setForm({...form,name:e.target.value})} />
      <input placeholder="Skills,comma-sep" value={form.skills} onChange={e=>setForm({...form,skills:e.target.value})} />
      <input placeholder="Education" value={form.education} onChange={e=>setForm({...form,education:e.target.value})} />
      <button onClick={handleUpload}>Create & Match</button>
      {error && <div style={{color:'red'}}>{error}</div>}

      {candidate && (
        <div>
          <h3>{candidate.name}</h3>
          <p>Skills: {candidate.skills.join(', ')}</p>
          <p>Education: {candidate.education}</p>
          <ExperienceList
            candidateId={candidate._id}
            experience={candidate.experience}
            onRefresh={async () => {
              const upd = await matchJobs(candidate._id);
              setJobs(upd.data);
            }}
          />
        </div>
      )}

      <h2>Matched Jobs</h2>
      <ul>{jobs.map(j=> <li key={j._id}>{j.title} [{j.requirements.join(', ')}]</li>)}</ul>
    </div>
  );
}
