import React, { useState } from 'react';
import { uploadResume, matchJobs } from '../api';

export default function UploadResume() {
  const [file, setFile]     = useState(null);
  const [name, setName]     = useState('');
  const [skills, setSkills] = useState('');
  const [education, setEdu] = useState('');
  const [cand, setCand]     = useState(null);
  const [jobs, setJobs]     = useState([]);
  const [err, setErr]       = useState('');

  const handle = async () => {
    setErr('');
    if (!file || !name) { setErr('Name and PDF required'); return; }
    const fd = new FormData();
    fd.append('resume', file);
    fd.append('name', name);
    fd.append('skills', skills);
    fd.append('education', education);

    try {
      const res = await uploadResume(fd);
      setCand(res.data);
      const m = await matchJobs(res.data._id);
      setJobs(m.data);
    } catch (e) {
      setErr(e.response?.data?.error || 'Error');
    }
  };

  return (
    <div className="mb-4">
      <h2 className="font-semibold">Upload Resume & Match</h2>
      <input type="text" placeholder="Name" value={name} onChange={e=>setName(e.target.value)} className="border px-2 py-1 mr-2" />
      <input type="text" placeholder="Skills,comma-sep" value={skills} onChange={e=>setSkills(e.target.value)} className="border px-2 py-1 mr-2" />
      <input type="text" placeholder="Education" value={education} onChange={e=>setEdu(e.target.value)} className="border px-2 py-1 mr-2" />
      <input type="file" onChange={e=>setFile(e.target.files[0])} className="border px-2 py-1 mr-2" />
      <button onClick={handle} className="bg-blue-600 text-white px-3 rounded">Upload & Match</button>
      {err && <div className="text-red-500 mt-2">{err}</div>}

      {cand && (
        <div className="mt-4">
          <h3 className="font-bold">{cand.name}</h3>
          <p>Skills: {cand.skills.join(', ')}</p>
          <p>Education: {cand.education}</p>
        </div>
      )}

      <h3 className="mt-4 font-semibold">Matched Jobs</h3>
      <ul className="list-disc ml-6">
        {jobs.map(j=> <li key={j._id}>{j.title}</li>)}
      </ul>
    </div>
  );
}
