import React, { useState } from 'react';
import { getJobs } from '../api';

export default function SkillSearch() {
  const [skill, setSkill] = useState('');
  const [jobs, setJobs]   = useState([]);
  const search = async () => {
    const res = await getJobs({ req: skill });
    setJobs(res.data);
  };
  return (
    <div>
      <h2 className="font-semibold">Search Jobs by Skill</h2>
      <input placeholder="Skill" value={skill} onChange={e=>setSkill(e.target.value)} className="border px-2 py-1 mr-2" />
      <button onClick={search} className="bg-green-600 text-white px-3 rounded">Search</button>
      <ul className="list-disc ml-6 mt-2">
        {jobs.map(j=> <li key={j._id}>{j.title}</li>)}
      </ul>
    </div>
  );
}
