import React, { useEffect, useState } from 'react';
import { getJobs, getAllCandidates } from '../api';

export default function AdminPage() {
  const [jobs, setJobs] = useState([]);
  const [cands, setCands] = useState([]);
  const [skillFilter, setSkillFilter] = useState('');

  useEffect(() => {
    fetchJobs();
    fetchCandidates();
  }, []);

  const fetchJobs = async () => {
    const res = await getJobs();
    setJobs(res.data);
  };

  const fetchCandidates = async (skill) => {
    const res = await getAllCandidates(skill ? { skill } : {});
    setCands(res.data);
  };

  return (
    <div>
      <h1 className="text-xl font-bold mb-4">Admin Dashboard</h1>

      <section className="mb-6">
        <h2 className="font-semibold">All Job Listings</h2>
        <ul className="list-disc ml-6">
          {jobs.map(j => <li key={j._id}>{j.title} [Reqs: {j.requirements.join(', ')}]</li>)}
        </ul>
      </section>

      <section>
        <h2 className="font-semibold mb-2">Search Candidates by Skill</h2>
        <input
          placeholder="Skill"
          value={skillFilter}
          onChange={e => setSkillFilter(e.target.value)}
          className="border px-2 py-1 mr-2"
        />
        <button onClick={() => fetchCandidates(skillFilter)} className="bg-green-500 text-white px-3 rounded">
          Search
        </button>
        <ul className="list-disc ml-6 mt-2">
          {cands.map(c => (
            <li key={c._id}>{c.name} [Skills: {c.skills.join(', ')}]</li>
          ))}
        </ul>
      </section>
    </div>
  );
}
