import React, { useEffect, useState } from 'react';
import { getJobs, getAllCandidates, createJob } from '../api';

export default function AdminPage() {
  const [jobs, setJobs] = useState([]);
  const [cands, setCands] = useState([]);
  const [skillFilter, setSkillFilter] = useState('');

  // New-job form state
  const [newTitle, setNewTitle] = useState('');
  const [newDesc, setNewDesc] = useState('');
  const [newReqs, setNewReqs] = useState('');
  const [jobError, setJobError] = useState('');

  useEffect(() => {
    fetchJobs();
    fetchCandidates();
  }, []);

  const fetchJobs = async () => {
    const res = await getJobs();
    setJobs(res.data);
  };

  const fetchCandidates = async (skill) => {
    const res = await getAllCandidates(skill ? { skill } : {});
    setCands(res.data);
  };

  const handleCreateJob = async () => {
    setJobError('');
    if (!newTitle || !newReqs) {
      setJobError('Title and requirements are required.');
      return;
    }
    try {
      const reqArray = newReqs.split(',').map(r => r.trim()).filter(Boolean);
      await createJob({ title: newTitle, description: newDesc, requirements: reqArray });
      // clear form & refresh
      setNewTitle('');
      setNewDesc('');
      setNewReqs('');
      fetchJobs();
    } catch (err) {
      setJobError(err.response?.data?.error || 'Failed to create job');
    }
  };

  return (
    <div>
      <h1 className="text-xl font-bold mb-4">Admin Dashboard</h1>

      {/* Job listings */}
      <section className="mb-6">
        <h2 className="font-semibold mb-2">All Job Listings</h2>
        <ul className="list-disc ml-6">
          {jobs.map(j => (
            <li key={j._id}>
              <strong>{j.title}</strong><br/>
              {j.description && <span>{j.description}<br/></span>}
              Reqs: {j.requirements.join(', ')}
            </li>
          ))}
        </ul>
      </section>

      {/* Add new job */}
      <section className="mb-6 p-4 border rounded">
        <h2 className="font-semibold mb-2">Add New Job</h2>
        <input
          placeholder="Job Title"
          value={newTitle}
          onChange={e => setNewTitle(e.target.value)}
          className="border px-2 py-1 mb-2 w-full"
        />
        <textarea
          placeholder="Description"
          value={newDesc}
          onChange={e => setNewDesc(e.target.value)}
          className="border px-2 py-1 mb-2 w-full"
        />
        <input
          placeholder="Requirements, comma-sep"
          value={newReqs}
          onChange={e => setNewReqs(e.target.value)}
          className="border px-2 py-1 mb-2 w-full"
        />
        <button
          onClick={handleCreateJob}
          className="bg-blue-600 text-white px-3 py-1 rounded"
        >
          Create Job
        </button>
        {jobError && <div className="text-red-500 mt-2">{jobError}</div>}
      </section>

      {/* Candidate search */}
      <section>
        <h2 className="font-semibold mb-2">Search Candidates by Skill</h2>
        <input
          placeholder="Skill"
          value={skillFilter}
          onChange={e => setSkillFilter(e.target.value)}
          className="border px-2 py-1 mr-2"
        />
        <button
          onClick={() => fetchCandidates(skillFilter)}
          className="bg-green-500 text-white px-3 rounded"
        >
          Search
        </button>
        <ul className="list-disc ml-6 mt-2">
          {cands.map(c => (
            <li key={c._id}>{c.name} [Skills: {c.skills.join(', ')}]</li>
          ))}
        </ul>
      </section>
    </div>
  );
}
